using AutoMapper;
using CleanArchitectureCQRS.Application.Features.CarFeatures.Command.CreateCar;
using CleanArchitectureCQRS.Application.Features.CarFeatures.Dtos;
using CleanArchitectureCQRS.Domain.Entities;

namespace CleanArchitectureCQRS.Persistence.Mappings;

public class MappingProfile : Profile
{
    public MappingProfile()
    {
        // Create your mappings here
        // Example: CreateMap<Source, Destination>();
        CreateMap<CreateCarCommand, Car>()
            .ForMember(dest => dest.Id, opt => opt.Ignore()); // Assuming Id is generated by the database;
        
        CreateMap<Car, CreateCarResponse>();
        
        
    }
}